public int findMaxLength(int[] nums) {
    int counter = 0;
    int max = 0;

    Map&lt;Integer,Integer&gt; map = new HashMap&lt;&gt;();
    map.put(0, 0);

    for(int i = 0; i &lt; nums.length; i++)
    {
        if(nums[i] == 0)
        {
            counter--;
        }

        if(nums[i] == 1)
        {
            counter++;
        }

        if(!map.containsKey(counter))
        {
            map.put(counter, i);
        }
        else
        {
            Integer firstIndex = map.get(counter);
            max = Math.max(max, i - firstIndex);
        }
    }
    return max;
}

} 
