class Solution {
    public boolean equalFrequency(String word) {

        Map&lt;Character, List&lt;Character&gt;&gt; grouped = word.chars()
        .mapToObj(i -&gt; (char) i)
        .collect(Collectors.groupingBy(o -&gt; o));


        Map&lt;Integer, List&lt;List&lt;Character&gt;&gt;&gt; groupByFreq = grouped.values()
        .stream()
        .collect(Collectors.groupingBy(o -&gt; o.size()));


        if(groupByFreq.size() &gt; 2)
        {

            return false;
        }

        if(groupByFreq.size() == 2)
        {
            List&lt;Integer&gt; freqSorted = groupByFreq
            .keySet()
            .stream()
            .sorted((e1, e2) -&gt; Integer.compare(e1, e2))
            .collect(Collectors.toList());

            return freqSorted.get(1) - freqSorted.get(0) == 1;
        }

        if(groupByFreq.size() == 1)
        {
            return groupByFreq.keySet().iterator().next() == 1;

        }

        return true;

    }
}

